#function
def getRecord():
	try:
		result = MKT_PAYABLE.query.filter_by(ID=g.formID).all()
		if not result:
			result = MKT_PAYABLE_INAU.query.filter_by(ID=g.formID).all()
		Dict={}
		if result:
			
			for row in result:
				Dict.update({'ID':g.formID})
				Dict.update({'Vendor'	:row.Vendor})
		
			return Dict
		else:
			return Dict
	except:
		raise
