@app.route('/Morakot/PayableSettlement/getInvoice',methods=['GET'])
@checkLogOutSession
@checkLogOutTime
def getPayableSettlementInvoice():
	Vendor 		= request.args.get('Vendor')
	Branch 		= mktuser.getCurrentBranch()
	dic 		= {}
	PayableID 	= ''
	Tran		= '_None'
	TranDesc	= '--Choose Transaction--'
	QueryObj	= MKT_PAYABLE.query.filter(	MKT_PAYABLE.OutstandingAmount>0,
											MKT_PAYABLE.Branch==Branch,
											MKT_PAYABLE.Vendor==Vendor).\
											order_by(MKT_PAYABLE.Invoice.asc(),)

	for row in QueryObj:
		if not row.ID[-1] == 'R':
			Description = row.Invoice + " - " +( row.Note[:35] +'...' if len(row.Note)> 35 else row.Note)
			dic[row.Invoice] = Description
			PayableID = row.ID

	param = mktarap.getARAPParam('APS')
	if param:
		Tran 	= param.ID
		TranDesc = ('%s - %s')%(param.ID,param.Description)

	return jsonify(		results=dic, 
						PayableID = PayableID,
						Tran 				=	Tran,
						TranDesc 			=	TranDesc
				)

def loadInvoice():
	if request.method == 'POST':
		Vendor = request.form["Vendor"]
		return MKT_PAYABLE.query.filter_by(Vendor=Vendor)
	else:
		if len(getRecord())>0:
			Record = getRecord()
			# print 'load Invoice with view'
			return MKT_PAYABLE.query.filter_by(ID=Record['ID'])  
		else:
			# print 'load Invoice with blank'
			return MKT_PAYABLE.query.filter_by(ID='')

def loadInvoiceChoise():  #define function 
	if request.method == 'POST':
		Vendor = request.form["Vendor"]
		QueryObj = MKT_PAYABLE.query.filter_by(Vendor=Vendor)
	else:
		if len(getRecord())>0:
			Record = getRecord()
			# print 'load Invoice with view'
			QueryObj =  MKT_PAYABLE.query.filter_by(ID=Record['ID'])  
		else:
			# print 'load Invoice with blank'
			QueryObj = MKT_PAYABLE.query.filter_by(ID='')

	ChoicesList = []
	if QueryObj:
		for row in QueryObj:
			Description = row.Invoice + " - " +( row.Note[:35] +'...' if len(row.Note)> 35 else row.Note)
			ChoicesList.append((row.Invoice, Description))
	return ChoicesList

