from app.mktcore.mdlimports import *

class MKT_RECEIVABLE(exmodel):
	ID					=	db.Column(db.String(14), primary_key=True)
	Customer			= 	db.Column(db.String(13))
	CustomerCategory	=	db.Column(db.String(15))
	Type				= 	db.Column(db.String(3))
	Invoice				=	db.Column(db.String(100),index=True)
	Currency			=	db.Column(db.String(3))
	Amount				= 	db.Column(db.Numeric(25,9))
	OutstandingAmount	= 	db.Column(db.Numeric(25,9))
	VAT 				= 	db.Column(db.String(1))
	VATAmount 			= 	db.Column(db.Numeric(25,9))
	VATCategory 		= 	db.Column(db.String(15))
	Transaction 		= 	db.Column(db.String(3))
	TranDate			= 	db.Column(db.String(10))
	Reference 			= 	db.Column(db.String(35))
	Note				= 	db.Column(db.Text)


