from app.mktcore.mdlimports import *

class MKT_RECEIVABLE_CUSTOMER(exmodel):
	ID					= db.Column(db.String(13), primary_key=True)
	CustomerName 		= db.Column(db.String(100))
	CustomerLocalName	= db.Column(db.String(100))
	CustomerCategory 	= db.Column(db.String(15))
	IsIndividual 		= db.Column(db.String(1))

	Salutation      	= db.Column(db.String(4))
	Gender				= db.Column(db.String(6))
	Resident			= db.Column(db.String(1))
	Registration 		= db.Column(db.Text())
	RegisterDate 		= db.Column(db.String(10))
	Remark 				= db.Column(db.String(150))

	Mobile1				= db.Column(db.String(35))
	Mobile2				= db.Column(db.String(35))
	Address1 			= db.Column(db.String(150))
	Address2 			= db.Column(db.String(150))
	Email 				= db.Column(db.String(35))
	Website 			= db.Column(db.String(35))
	Facebook 			= db.Column(db.String(50))
	LineID 				= db.Column(db.String(35))
	WhatsApp 			= db.Column(db.String(35))
	Telegram 			= db.Column(db.String(35))
	Viber 				= db.Column(db.String(35))


