class MKT_ACC_POSTING_DE(exmodel):
	ID 				=	db.Column(db.String(20), primary_key=True)
	DEID 			= 	db.Column(db.Integer, primary_key=True, autoincrement=True)
	Category 		= 	db.Column(db.String(15))
	Currency 		=	db.Column(db.String(3))
	AssetClass 		= 	db.Column(db.String(3))
	MoreThanOneYear	=	db.Column(db.String(1))
	ConsolKey 		= 	db.Column(db.String(140))
	AccountID 		= 	db.Column(db.String(15))
	DebitCredit		= 	db.Column(db.String(2))
	Transaction		= 	db.Column(db.String(5))
	Module			= 	db.Column(db.String(5))
	Amount			=	db.Column(db.Numeric(25, 9))
	LCYAmount 		= 	db.Column(db.Numeric(25, 9))

