from app.mktcore.mdlimports import *

class MKT_ACC_PRODUCT(exmodel):
	ID 					= 	db.Column(db.String(3), primary_key=True, index=True)
	Description 		= 	db.Column(db.String(35))
	CategoryList 		= 	db.Column(db.String(15))
	InterestKey 		= 	db.Column(db.String(6))
	Charge 				= 	db.Column(db.String(6))
	Tax 				= 	db.Column(db.String(3))
	Rule 				= 	db.Column(db.String(6))
	IntDayBasis 		= 	db.Column(db.String(1))
	IntAccrBasis 		= 	db.Column(db.String(1))
	IntCapitalization 	= 	db.Column(db.String(1))
	ClosingWithAccr 	=	db.Column(db.String(1), default='Y')
	DormantDays 		= 	db.Column(db.String(3))
	ProductType 		=	db.Column(db.String(1))
	IntExpenseCat 		=	db.Column(db.String(15)) # Int Expense
	IntPayableCat 		=	db.Column(db.String(15)) # Accr Interest Payable
	AccrReceivableCat 	=	db.Column(db.String(15)) # Accr Interest Receivable
	IntIncomeCat 		=	db.Column(db.String(15)) # Int Income
	AccrMode 			=	db.Column(db.String(10)) # Accrue mode

