from app.mktcore.mdlimports import *

class MKT_BATCH_JOB(exmodel):
	ID 				= 	db.Column(db.String(4), primary_key=True)
	Description 	= 	db.Column(db.String(50))
	Tool	 		= 	db.Column(db.String(50))
	JobStatus 		= 	db.Column(db.String(1))
	Frequency 		= 	db.Column(db.String(10))
	Enable			= 	db.Column(db.String(3))
	Dependency 		=	db.Column(db.String(4))
	Order 			=	db.Column(db.String(4))
	LastRunDate		= 	db.Column(db.String(30))
	NextRunDate 	= 	db.Column(db.String(30))

