from app.mktcore.mdlimports import *

class MKT_BJ_STAT(exmodel):
	ID 				= 	db.Column(db.String(20), primary_key=True)
	JobID 			=	db.Column(db.String(4))
	Module 			= 	db.Column(db.String(3))
	Description		= 	db.Column(db.String(50))
	StartTime 		= 	db.Column(db.String(10))
	EndTime 		= 	db.Column(db.String(10))
	Duration		= 	db.Column(db.String(10))
	SystemDate		= 	db.Column(db.String(10))
	CalendarDate	=	db.Column(db.String(10))
	NumberOfRecord 	=	db.Column(db.Integer)
	Completed 		=	db.Column(db.Numeric(5, 2))
	Order 			=	db.Column(db.Numeric(5, 2))
	NumberOfCompleted = db.Column(db.Integer)
	Progress 		=	db.Column(db.String(1))