from app.mktcore.wtfimports import *
from .models 				import *
from app.Category.models 	import *


def loadCategory():
	return MKT_CATEGORY.query


class FRM_BORROWING_CHARGE(exform):
	Description 	= TextField(requiredlabel("Description", "*"),
								validators=[validators.Required(), validators.Length(max=100)]
								)

	ChargeCategory 	= QuerySelectField(requiredlabel("Category", "*"),
										query_factory=loadCategory,
										get_label=u'Description',
										allow_blank=True,
										blank_text='--Choose Charge Category--',
										validators=[validators.Required()]
									  )
