from app.mktcore.mdlimports import *

class MKT_BORROWING(exmodel):
	ID 					= db.Column(db.String(14), primary_key=True)
	Lender 				= db.Column(db.String(12))
	ExternalAccount 	= db.Column(db.String(20))
	Currency 			= db.Column(db.String(3))
	DisbursedAmount 	= db.Column(db.Numeric(25,9))
	OutstandingAmount 	= db.Column(db.Numeric(25,9))
	RegChargeCategory 	= db.Column(db.String(15))
	BankCategory		= db.Column(db.String(15))
	PayableCategory 	= db.Column(db.String(15))
	IntRate 			= db.Column(db.String(11))
	DrAccount 			= db.Column(db.String(20))
	IntExpAIP 			= db.Column(db.String(15))
	Charge1 			= db.Column(db.String(15))
	ChargeAmount1 		= db.Column(db.Numeric(25,9))
	Charge2				= db.Column(db.String(15))
	ChargeAmount2 		= db.Column(db.Numeric(25,9))
	Reference 			= db.Column(db.String(14))
	DisbursedDate 		= db.Column(db.String(10))
	# TranDate 			= db.Column(db.String(10))
	ValueDate 			= db.Column(db.String(10))
	MaturityDate 		= db.Column(db.String(10))		
	Note 				= db.Column(db.String(100))


