from app.mktcore.mdlimports import *

class MKT_BORROWING_PAYMENT(exmodel):
	ID   			= db.Column(db.String(14), primary_key=True)
	BorrowingID 	= db.Column(db.String(14))
	Installment 	= db.Column(db.String(4))
	Currency 		= db.Column(db.String(3))
	Principal 		= db.Column(db.Numeric(25,9))
	Interest 		= db.Column(db.Numeric(25,9))
	RegularCharge 	= db.Column(db.Numeric(25,9))
	TotalAmount 	= db.Column(db.Numeric(25,9))
	AccrInterest 	= db.Column(db.Numeric(25,9))	
	CrAccount 		= db.Column(db.String(20))
	CrCategory 		= db.Column(db.String(15))
	Charge1    		= db.Column(db.String(3))
	ChargeAmount1 	= db.Column(db.Numeric(25,9))
	Charge2    		= db.Column(db.String(3))
	ChargeAmount2 	= db.Column(db.Numeric(25,9))
	TaxCategory 	= db.Column(db.String(15))
	TaxRate 		= db.Column(db.String(11))
	TaxAmount 		= db.Column(db.Numeric(25,9))
	TranDate 		= db.Column(db.String(10))
	Transaction 	= db.Column(db.String(3))
	Note 			= db.Column(db.String(100))
	Reference 		= db.Column(db.String(14))
	Terminate  		= db.Column(db.String(1))


