from app.mktcore.mdlimports import *


class MKT_BORROWING_SCHEDULE(exmodel):
	ID 					= db.Column(db.String(20), primary_key=True)
	BorrowingID 		= db.Column(db.String(14))
	InstallmentNo 		= db.Column(db.Integer)
	CollectionDate 		= db.Column(db.String(10))
	Principal 			= db.Column(db.Numeric(25,9))
	Interest 			= db.Column(db.Numeric(25,9))
	Charge 				= db.Column(db.Numeric(25,9))
	Balance 			= db.Column(db.Numeric(25,9))
	BorrowingStatus 	= db.Column(db.String(1))
	PaidAmount			= db.Column(db.Numeric(25,9))
	

