from app.mktcore.mdlimports import *

class MKT_LENDER(exmodel):
	ID 					= db.Column(db.String(12), primary_key=True)
	CompanyFullName 	= db.Column(db.String(100))
	CompanyShortName 	= db.Column(db.String(35))
	RepresentativeName	= db.Column(db.String(50))
	RegisterNumber		= db.Column(db.String(15))
	RegisterDate 		= db.Column(db.String(10))
	Country 			= db.Column(db.String(2))
	Address 			= db.Column(db.String(200))
	ContactNumber 		= db.Column(db.String(15))
	ContactPerson 		= db.Column(db.String(50))
	Email 				= db.Column(db.String(50))
	Website 			= db.Column(db.String(50))
	Facebook 			= db.Column(db.String(50))


