from app.mktcore.mdlimports import *


class MKT_CBC(exmodel):
	ID 						= 	db.Column(db.String(20), primary_key=True)
	AccountType 			=	db.Column(db.String(11))
	LoanAppID				= 	db.Column(db.String(13))
	EnquiryType 			=	db.Column(db.String(2))
	NumberOfApplication		=	db.Column(db.String(3))
	Currency 				=	db.Column(db.String(3))
	Amount 					=	db.Column(db.Numeric(25, 9))
	Action  				=	db.Column(db.String(5))

