class MKT_CBC_DE(exmodel):
	ID 						= 	db.Column(db.String(20), primary_key=True, index=True)
	DetailID				= 	db.Column(db.Integer, primary_key=True, autoincrement=True)
	Customer				= 	db.Column(db.String(35))
	MemberReference 		=	db.Column(db.String(20))
	SuccessDate 			=	db.Column(db.String(20))
	#ENQUIRY_NO 	ID generated enquiry serial number from CBC
	EnquiryID				= 	db.Column(db.String(11))
	ApplicationType			=	db.Column(db.String(1))

