from app.mktcore.mdlimports import *

class MKT_CASH_ACCOUNT(exmodel):
	ID = db.Column(db.String(8), primary_key=True, index=True)
	Type = db.Column(db.String(1), default='V')
	Currency = db.Column(db.String(35))
	Account = db.Column(db.Text)
	CashBranch = db.Column(db.String(6))
	Description = db.Column(db.String(65))

