from app.mktcore.mdlimports import *

class MKT_CHARGE_RATE(exmodel):
	ID 				= db.Column(db.String(6), primary_key=True, index=True)
	Description 	= db.Column(db.String(50))
	ChargeMode 		= db.Column(db.String(3))
	RegularFeeType  = db.Column(db.String(3))
	RateFixed 		= db.Column(db.String(1))
	Tax				= db.Column(db.String(3))
	Value 			= db.Column(db.String(100))
	MinimumAmount	= db.Column(db.Numeric(18, 2))
	ReceiveCat 		= db.Column(db.String(15))
	IncomeCat 		= db.Column(db.String(15))
	ChargeSuspendCat= db.Column(db.String(15))
	Amortize 		= db.Column(db.String(1))

