class MKT_CONSOL_BALANCE(exmodel):
	ID 						= 	db.Column(db.String(335), primary_key=True, index=True)
	Branch 					=	db.Column(db.String(6), primary_key=True, index=True)
	Currency 				=	db.Column(db.String(3))
	PrevMonthBal 			=	db.Column(db.Numeric(25, 9))
	CurrentPrevMonthBal 	=	db.Column(db.Numeric(25, 9))
	CurrentMonthBal 		=	db.Column(db.Numeric(25, 9))
	PrevYearBal 			=	db.Column(db.Numeric(25, 9))
	CurrentPrevYearBal 		=	db.Column(db.Numeric(25, 9))
	YTDBal					=	db.Column(db.Numeric(25, 9))
	Balance					=	db.Column(db.Numeric(25, 9))

	LCYPrevMonthBal 		=	db.Column(db.Numeric(25, 9))
	LCYCurrentPrevMonthBal 	=	db.Column(db.Numeric(25, 9))
	LCYCurrentMonthBal 		=	db.Column(db.Numeric(25, 9))
	LCYPrevYearBal 			=	db.Column(db.Numeric(25, 9))
	LCYCurrentPrevYearBal 	=	db.Column(db.Numeric(25, 9))
	LCYYTDBal				=	db.Column(db.Numeric(25, 9))
	LCYBalance				=	db.Column(db.Numeric(25, 9))