from app.mktcore.mdlimports import *

class MKT_BILL_PROVIDER(exmodel):
	ID			    =	db.Column(db.String(10), primary_key=True)
	CustomerID 	    =   db.Column(db.String(12))
	Description     = 	db.Column(db.Text())
	LocalDescription= 	db.Column(db.Text())
	ProviderType    =	db.Column(db.String(14))
	Logo    	    =	db.Column(db.Text())
	Active  	    =	db.Column(db.String(1))
	Category		=	db.Column(db.String(15))
	CredentialType 	= 	db.Column(db.Text())
	CredentialID 	= 	db.Column(db.Text())

