from app.mktcore.mdlimports import *

class MKT_CARD(exmodel):
	ID			=	db.Column(db.String(10), primary_key=True)
	CustomerID 	=   db.Column(db.String(12))
	CardNo	    =	db.Column(db.String(65))
	Cvv	    	=	db.Column(db.String(5))
	Description = 	db.Column(db.Text())
	CardType    =	db.Column(db.String(3))
	AccountLink	=	db.Column(db.String(20))
	Active  	=	db.Column(db.String(1))
	ExpiryDate  = 	db.Column(db.String(20))
	Background	= 	db.Column(db.Text())
	Logo		= 	db.Column(db.Text())

