from app.mktcore.mdlimports import *
#MKT_CURRENCY
class MKT_CURRENCY(exmodel):
    ID              = db.Column(db.String(3), primary_key=True)
    Description     = db.Column(db.String(50))
    BuyRate         = db.Column(db.Numeric(41, 16))
    SellRate        = db.Column(db.Numeric(41, 16))
    MidRate         = db.Column(db.Numeric(41, 16))
    RndMode         = db.Column(db.String(1))
    SmallestRndTo   = db.Column(db.Integer)
    DecimalPlace    = db.Column(db.Integer)
    ThoSeparator    = db.Column(db.String(1))
    CurrencySign    = db.Column(db.String(1))
    DecSeparator    = db.Column(db.String(1))
    ReportingRate   = db.Column(db.Numeric(41, 16))
    OtherRate1      = db.Column(db.String(40))
    OtherRate2      = db.Column(db.String(40))
    OtherRate3      = db.Column(db.String(40))

