from app.mktcore.mdlimports import *

class MKT_CUSTOMER_ACT(exmodel):
	ID				=	db.Column(db.String(15), primary_key=True)
	CustomerAct 	=	db.Column(db.String(15))
	Customer		=	db.Column(db.String(15))
	FollowUpDate	=	db.Column(db.String(27))
	ActivityType	=	db.Column(db.String(6))
	ActivityDescr	=	db.Column(db.String(100))
	DateOfActivity 	=	db.Column(db.String(27))
	RootCause 		=	db.Column(db.String(5))
	Comment 		=	db.Column(db.Text)
	AcStatus		= 	db.Column(db.String(5))
	ActNo 			=	db.Column(db.String(10),default='0')


