from app.mktcore.mdlimports import *

class MKT_DAILY_BOOKING(exmodel):
	ID 					= 	db.Column(db.String(20), primary_key=True, index=True)
	Customer			=	db.Column(db.String(12),index=True)
	Reference 			= 	db.Column(db.String(15),index=True)
	BaseAmount 			= 	db.Column(db.Numeric(25, 9))
	Rate				= 	db.Column(db.String(15))
	NumDay				=	db.Column(db.Integer)
	Currency 			= 	db.Column(db.String(3))
	BookedAmount		=	db.Column(db.Numeric(25,9))
	Module 				= 	db.Column(db.String(10))
	Transaction 		=	db.Column(db.String(3))
	TransactionDate 	= 	db.Column(db.String(10),index=True)

class MKT_DAILY_BOOKING_HIST(exmodel):
	ID 					= 	db.Column(db.String(25), primary_key=True, index=True)
	Customer			=	db.Column(db.String(12),index=True)
	Reference 			= 	db.Column(db.String(15),index=True)
	BaseAmount 			= 	db.Column(db.Numeric(25, 9))
	Rate				= 	db.Column(db.String(15))
	NumDay				=	db.Column(db.Integer)
	Currency 			= 	db.Column(db.String(3))
	BookedAmount		=	db.Column(db.Numeric(25,9))
	Module 				= 	db.Column(db.String(10))
	Transaction 		=	db.Column(db.String(3))
	TransactionDate 	= 	db.Column(db.String(10),index=True)