from app.mktcore.wtfimports import *
from .models import *
import time
from app.Province.models import *

def loadProvince():
	return MKT_PROVINCE.query

#class FRM_EMPLOYEE
class FRM_EMPLOYEE(exform):
	Male		=TextField("Male")
	Female		=TextField("Female")
	Province	=QuerySelectField(requiredlabel('Province','*'),query_factory=loadProvince, 
					get_label='Description',#Field Name
					allow_blank=True,
					blank_text='--None--',
					validators=[validators.Required()])

	
	def validate_Male(form,field):		
		try:
			Male=int(request.form['Male'])
		except ValueError:
			raise ValidationError("This field must be in numeric format.")

	def validate_Female(form,field):
		try:
			Female = int(request.form['Female'])
		except ValueError:
			raise ValidationError("This field must be in numeric format.")

