from app.mktcore.wtfimports import *
from .models import *
from app.Transaction.models import MKT_TRANSACTION


def loadTransaction():
	return MKT_TRANSACTION.query.all()

#class FRM_ASSET_ACQUISITION
class FRM_ASSET_ACQUISITION(exform):
	Description = TextAreaField(requiredlabel('Description','*'), 
								validators=[validators.Required(), 
								validators.Length(max=50)]
								)
	Transaction = QuerySelectField(requiredlabel("Transaction","*"),
									query_factory=loadTransaction, 
									blank_text=u'--Choose Transaction--',
									get_label=u'Description', 
									allow_blank=True, 
									validators=[validators.Required()]
									)