from app.mktcore.mdlimports import *

class MKT_ASSET_ASSIGNING(exmodel):
	ID 					= db.Column(db.String(14), primary_key=True)
	RecordType			= db.Column(db.String(2))
	AssigneeID			= db.Column(db.String(10))
	NewAssigneeID		= db.Column(db.String(10))
	AssetID 			= db.Column(db.String(14))
	OldBranch           = db.Column(db.String(6))
	NewBranch           = db.Column(db.String(6))
	SerialNumber 		= db.Column(db.String(35))
	AssigningDate   	= db.Column(db.String(10))
	StopUsingDate   	= db.Column(db.String(10))
	Note 				= db.Column(db.String(100))
	

