from app.mktcore.mdlimports import *

class MKT_ASSET_DISPOSE(exmodel):
	ID 						= db.Column(db.String(14), primary_key=True)
	AssetID 				= db.Column(db.String(14))
	SerialNumber 			= db.Column(db.String(35), unique=True)
	DisposalType			= db.Column(db.String(3))
	DrAccount 				= db.Column(db.String(20))
	PaymentMethod			= db.Column(db.String(3))
	MaturityDate 			= db.Column(db.String(10))
	DisposeDate 			= db.Column(db.String(10))
	Currency				= db.Column(db.String(5))
	AccumulatedDepreciation = db.Column(db.Numeric(25,9))
	AssetPrice				= db.Column(db.Numeric(25,9))
	EndBookValue 			= db.Column(db.Numeric(25,9))
	Amount					= db.Column(db.Numeric(25,9))
	LossOrGainAmount		= db.Column(db.Numeric(25,9))
	Note 					= db.Column(db.String(100))

