from app.mktcore.mdlimports import *

class MKT_ASSET_SETUP(exmodel):
	ID 						= db.Column(db.String(14), primary_key=True)
	Description 			= db.Column(db.String(50))
	AssetGroup 				= db.Column(db.String(3))
	AssetType 				= db.Column(db.String(3))
	AcquisitionType 		= db.Column(db.String(3))
	AssetLife 				= db.Column(db.String(2))
	DepreciationMethod 		= db.Column(db.String(3))
	DepDayBasis 			= db.Column(db.String(5))
	AssetCategory 			= db.Column(db.String(15))
	DepreciationCategory 	= db.Column(db.String(15))
	AccumulatedCategory 	= db.Column(db.String(15))
	AccumulatedAmortization = db.Column(db.String(15))
	AmortizationCategory 	= db.Column(db.String(15))
	LossOnDisposalCategory 	= db.Column(db.String(15))
	GainOnDisposalCategory 	= db.Column(db.String(15))
	RepairCategory 			= db.Column(db.String(15))
	Currency 				= db.Column(db.String(3))
	StartPrice 				= db.Column(db.Numeric(25,9))
	EndPrice 				= db.Column(db.Numeric(25,9))
	Frequency 				= db.Column(db.String(1))

