from app.mktcore.wtfimports import *
from .models 				import *
from ..AssetGroup.models 	import *


def loadAssetGroup():
	return MKT_ASSET_GROUP.query.all()


# class FRM_FAM_ASSET_TYPE
class FRM_ASSET_TYPE(exform):
	Description = TextAreaField(requiredlabel('Description','*'), 
								validators=[validators.Required(), 
								validators.Length(max=100)]
								)
	Type        = SelectField(	requiredlabel("Type","*"), 
								choices=[("T","Tangible"),("I","Intangible")], 
								validators=[validators.Required()]
								)
	IsStationary = SelectField(	requiredlabel("Is Stationary Asset","*"),
								choices=[("N","No"),("Y","Yes")],
								validators=[validators.Required()]
								)
