from app.mktcore.mdlimports import *

class MKT_ASSET_DEPRE_SCHEDULE(exmodel):
	ID 						= db.Column(db.String(14), primary_key=True, index=True)
	InstallmentNo 			= db.Column(db.String(6), primary_key=True)
	StartDate				= db.Column(db.String(10), index=True)
	EndDate 				= db.Column(db.String(10), index=True)
	BeginBookValue 			= db.Column(db.Numeric(25,9))
	Depreciation 			= db.Column(db.Numeric(25,9))
	AccumulatedDepreciation = db.Column(db.Numeric(25,9))
	EndBookValue 			= db.Column(db.Numeric(25,9))
	DepreciationStatus 		= db.Column(db.String(1), server_default="n")

