from app.mktcore.mdlimports import *

class MKT_ASSET(exmodel):
	ID 			  					= db.Column(db.String(14), primary_key=True, index=True)
	RecordType						= db.Column(db.String(2))
	SerialNumber					= db.Column(db.String(35), index=True, default=None, unique=True)
	AssigneeID						= db.Column(db.String(10))
	AssigningDate					= db.Column(db.String(10))
	AssetStatus						= db.Column(db.String(5))
	Reference						= db.Column(db.String(14))
	Vendor 							= db.Column(db.String(14))
	AssetGroup 						= db.Column(db.String(3))
	AssetType 						= db.Column(db.String(3))
	AcquisitionType    				= db.Column(db.String(3))
	Description 					= db.Column(db.String(100))
	Currency 						= db.Column(db.String(3))
	Model 							= db.Column(db.String(20))
	Brand							= db.Column(db.String(20))
	DepreciationMethod 				= db.Column(db.String(50))
	UnitOfProOrHourOfOpr 			= db.Column(db.Numeric(25,9))
	WholeLifeUnitOfProOrHourOfOpr 	= db.Column(db.Numeric(25,9))
	PercentageDep 					= db.Column(db.String(11))
	AssetLife 						= db.Column(db.String(5))
	AssetCategory 					= db.Column(db.String(15))
	PaymentMethod					= db.Column(db.String(3))
	CrAccount						= db.Column(db.String(20))
	SalvageValue 					= db.Column(db.Numeric(25, 9))
	Total							= db.Column(db.Numeric(25, 9))
	ValueDate 						= db.Column(db.String(10))
	MaturityDate 					= db.Column(db.String(10))
	Frequency 						= db.Column(db.String(10))
	LastRunDate						= db.Column(db.String(10))
	NextRunDate         			= db.Column(db.String(10))
	BeginningBookValue				= db.Column(db.Numeric(25,9))
	AccumulatedDepreciation			= db.Column(db.Numeric(25,9))
	EndBookValue					= db.Column(db.Numeric(25,9))
	PreviousDepAmount				= db.Column(db.Numeric(25,9))
	CurrentDepAmount				= db.Column(db.Numeric(25,9))
	AssetSetup 						= db.Column(db.String(14))
	AssetGroup 						= db.Column(db.String(3))
	AssetType 						= db.Column(db.String(3))
	AcquisitionType 				= db.Column(db.String(3))
	DepreciationMethod 				= db.Column(db.String(3))
	DepDayBasis 					= db.Column(db.String(5))
	AssetCategory 					= db.Column(db.String(15))
	DepreciationCategory 			= db.Column(db.String(15))
	AccumulatedCategory 			= db.Column(db.String(15))
	AccumulatedAmortization 		= db.Column(db.String(15))
	AmortizationCategory 			= db.Column(db.String(15))
	LossOnDisposalCategory 			= db.Column(db.String(15))
	GainOnDisposalCategory 			= db.Column(db.String(15))
	RepairCategory 					= db.Column(db.String(15))


