class MKT_ASSET_CONSUMPTION(exmodel):
	ID 					= db.Column(db.String(14),primary_key=True)
	ConsumingID 		= db.Column(db.Integer, primary_key=True, autoincrement=True) #autoincrement is automatically set to True if field data type is Integer and primary_key=True
	Remark		 		= db.Column(db.Text())
	PurchasePriceUnits 	= db.Column(db.Numeric(25, 9))
	Quantity 			= db.Column(db.Integer())
	Remaining 			= db.Column(db.Integer())
	Discount 			= db.Column(db.String(11))
	Amount 				= db.Column(db.Numeric(25, 9))

