from app.mktcore.mdlimports import *

class MKT_VENDOR(exmodel):
	# __table_args__ = {'extend_existing': True} 
	ID 				= db.Column(db.String(14), primary_key=True)
	VendorBranch 	= db.Column(db.String(3))
	VendorName 		= db.Column(db.String(50))
	VendorKhmerName = db.Column(db.String(50))
	CrAccount 		= db.Column(db.String(20))
	PayableCategory = db.Column(db.String(15))
	VendorType 		= db.Column(db.String(20))
	PhoneNumber 	= db.Column(db.String(20))
	Email 			= db.Column(db.String(50))
	Facebook 		= db.Column(db.String(50))
	LineID 			= db.Column(db.String(25))
	WhatsApp 		= db.Column(db.String(15))
	Telegram 		= db.Column(db.String(15))
	Viber 			= db.Column(db.String(15))
	Website 		= db.Column(db.String(30))
	CorporationDate = db.Column(db.String(10))
	Country 		= db.Column(db.String(3))
	Village 		= db.Column(db.String(8))
	Commune 		= db.Column(db.String(6))
	District 		= db.Column(db.String(4))
	Province 		= db.Column(db.String(2))
	MOUSign 		= db.Column(db.String(1))
	Remark 			= db.Column(db.String(100))

