from datetime import datetime


def DaysDiff(DateOffSet, CurrentDate):
    DateOffSet = datetime.strptime(DateOffSet, "%Y-%m-%d")
    CurrentDate = datetime.strptime(CurrentDate, "%Y-%m-%d")
    return abs((CurrentDate - DateOffSet).days)


def AverageYear(Days, DayBasis):
	Years = round((Days / float(DayBasis)), 1)
	return Years