from app.mktcore.mdlimports import *

class MKT_FOREIGN_EXCHANGE(exmodel):
	ID 				= db.Column(db.String(15), primary_key=True, index=True)
	DrAccount 		= db.Column(db.String(20))
	DrCategory 		= db.Column(db.String(15))
	DrCurrency 		= db.Column(db.String(3))
	CrAccount 		= db.Column(db.String(20))
	CrCategory 		= db.Column(db.String(15))
	CrCurrency 		= db.Column(db.String(3))
	InputCurrency	= db.Column(db.String(1))
	Amount 			= db.Column(db.Numeric(25, 9))
	ExchangeRate	= db.Column(db.String(40))
	ExchangeAmount 	= db.Column(db.Numeric(25, 9))
	Transaction 	= db.Column(db.String(3))
	TranDate 		= db.Column(db.String(10))
	Reference 		= db.Column(db.String(35))
	Note 			= db.Column(db.String(100))

