from app.mktcore.mdlimports import *

class MKT_JOURNAL(exmodel):
	ID 					= 	db.Column(db.String(25), primary_key=True, index=True)
	Description 		= 	db.Column(db.String(100))
	CategoryID 			= 	db.Column(db.String(15), index=True)
	CustomerID 			= 	db.Column(db.String(12), index=True)
	DebitCredit 		= 	db.Column(db.String(2))
	Amount 				= 	db.Column(db.Numeric(25, 9))
	PrevBalance 		=	db.Column(db.Numeric(25, 9)) # PrevBalance for Beggining Balance on GL Balance Detail
	Currency 			= 	db.Column(db.String(3))
	Module 				= 	db.Column(db.String(10))
	Transaction 		=	db.Column(db.String(3), index=True)
	TransactionDate 	= 	db.Column(db.String(20), index=True)
	Reference 			= 	db.Column(db.String(15), index=True)
	UserReference 		= 	db.Column(db.String(50), index=True)
	GL_KEYS 			= 	db.Column(db.String(335), index=True)
	LCYAmount 			= 	db.Column(db.Numeric(25, 9))
	LCYPrevBalance 		=	db.Column(db.Numeric(25, 9))
	Account 			= 	db.Column(db.String(20))

