from app.mktcore.mdlimports import *

class MKT_JOURNAL_ENTRY(exmodel):
	ID 					= 	db.Column(db.String(14), primary_key=True, index=True)
	SpecialJournal 		= 	db.Column(db.String(14))
	DebitCategory 		= 	db.Column(db.String(15))
	CreditCategory 		= 	db.Column(db.String(15))
	DrAccount 			=	db.Column(db.String(20))
	CrAccount 			=	db.Column(db.String(20))
	Amount 				= 	db.Column(db.Numeric(25, 9))
	Currency 			= 	db.Column(db.String(3))
	Transaction 		= 	db.Column(db.String(3))
	TransactionDate 	= 	db.Column(db.String(20))
	Reference 			= 	db.Column(db.String(35))
	Note 				= 	db.Column(db.String(100))
	K1 					= 	db.Column(db.String(35))
	K2 					= 	db.Column(db.String(35))
	K3 					= 	db.Column(db.String(35))
	K4 					= 	db.Column(db.String(35))
	K5 					= 	db.Column(db.String(35))
	K6 					= 	db.Column(db.String(35))
	K7 					= 	db.Column(db.String(35))
	K8 					= 	db.Column(db.String(35))
	K9 					= 	db.Column(db.String(35))
	LCYAmount 			= 	db.Column(db.Numeric(25, 9))
	ExchangeRate		= 	db.Column(db.String(40))
	VoucherType 		= 	db.Column(db.String(3))
	#2018-06-05 - Inter-Branch
	TargetBranch 		=	db.Column(db.String(6))
	TargetBranchDrCr	=	db.Column(db.String(2))
	#end

