from app.mktcore.mdlimports import *

class MKT_LOAN_AMENDMENT(exmodel):

	ID 				= 	db.Column(db.String(13), primary_key=True)
	LoanID 			= 	db.Column(db.String(13))
	Customer 		=	db.Column(db.String(70))
	VB 				=	db.Column(db.String(50))
	Operation 		= 	db.Column(db.String(3)) #TMN = Termination, AMT = Admenment
	AddDeduct		=	db.Column(db.String(1))
	Amount 			=	db.Column(db.Numeric(25, 9))
	Currency 		=	db.Column(db.String(3))
	InterestOld 	= 	db.Column(db.String(11))
	Interest 		=	db.Column(db.String(11))
	FreqTypeOld 	= 	db.Column(db.String(1))
	FreqType 		= 	db.Column(db.String(1))
	FrequencyOld 	= 	db.Column(db.String(3))
	Frequency 		= 	db.Column(db.String(3))
	InstallmentOld 	=	db.Column(db.String(4))
	Installment 	=	db.Column(db.String(4))
	TermOld 		=	db.Column(db.String(4))
	Term 			=	db.Column(db.String(4))
	AmendDate 		=	db.Column(db.String(20))
	LoanType		= 	db.Column(db.String(1), default='R')
	LastPaidAmount 	= 	db.Column(db.Numeric(25, 9))
	InstallAmount 	= 	db.Column(db.Numeric(25, 9))
	KeepPrevSchedule= 	db.Column(db.String(1))
	Reason 			=	db.Column(db.Text)
	PenaltyAmount 	= 	db.Column(db.Numeric(25, 9))
	WaiveInterest	=	db.Column(db.Numeric(25, 9))
	CollectCurrInt	=	db.Column(db.Numeric(25, 9))
	PrevDisbursed 	=	db.Column(db.Numeric(25, 9))
	Restructure 	=	db.Column(db.String(1), default='Y')
	#Capitalize Interest/Penalty 2018-04-11 Mony
	CapitalizeAmount	=	db.Column(db.Numeric(25, 9))
	CapitalizeOption	=	db.Column(db.String(1), default='N')
	#end
	AmendmentStat 	=	db.Column(db.String(1), default='Y')


