from app.mktcore.mdlimports import *

class MKT_LOAN_APPLICATION(exmodel):
	ID 				= db.Column(db.String(13), primary_key=True)
	LNCustomerID 	= db.Column(db.String(10))
	AppDate 		= db.Column(db.String(20))
	Currency 		= db.Column(db.String(3))
	AppliedAmount	= db.Column(db.Numeric(25, 9)) # add by sophiarith on 2016-07-05
	Amount 			= db.Column(db.Numeric(25, 9))
	Installment 	= db.Column(db.String(4))
	Term 			= db.Column(db.String(4))
	Cycle 			= db.Column(db.String(2))
	LoanProduct 	= db.Column(db.String(3))
	Category 		= db.Column(db.String(15))
	InterestRate 	= db.Column(db.String(11))
	FreqType 		= db.Column(db.String(1))
	Frequency 		= db.Column(db.String(3))
	LoanPurpose 	= db.Column(db.String(3))
	VBID 			= db.Column(db.String(10))
	Group 			= db.Column(db.String(15))
	SourceOfFund 	= db.Column(db.String(4))
	AppStatus 		= db.Column(db.String(1))
	PreparedBy 		= db.Column(db.String(35))
	PreparedDate 	= db.Column(db.String(20))
	ReviewedBy 		= db.Column(db.String(35))
	ReviewedDate 	= db.Column(db.String(20))
	ApprovedBy 		= db.Column(db.String(35))
	ApprovedDate 	= db.Column(db.String(20))
	RejectedBy 		= db.Column(db.String(35))
	RejectedDate 	= db.Column(db.String(20))
	RestrictAuth 	= db.Column(db.String(15))
	#CBC 			= db.Column(db.String(3), default='N')

	#For Leasing
	Vendor					= db.Column(db.String(13))
	AssetPrice				= db.Column(db.Numeric(25,9))
	DownPayment				= db.Column(db.Numeric(25,9))
	VATInput				= db.Column(db.Numeric(25,9))
	VATOutputOnLoan			= db.Column(db.Numeric(25,9))
	VATOutputOnDownPayment	= db.Column(db.Numeric(25,9))
	TaxExpense				= db.Column(db.Numeric(25,9))
	AssetDescription		= db.Column(db.Text)


