from app.mktcore.mdlimports import *

class MKT_LOAN_CONTRACT(exmodel):
	ID 					= 	db.Column(db.String(13), primary_key=True, index=True)
	LoanApplicationID 	= 	db.Column(db.String(13))
	ContractCustomerID 	= 	db.Column(db.String(12)) #Customer ID
	Account 			= 	db.Column(db.String(20))
	Currency 			= 	db.Column(db.String(3))
	Amount 				= 	db.Column(db.Numeric(25, 9))
	Disbursed 			= 	db.Column(db.Numeric(25, 9))
	ApprovedAmount		= 	db.Column(db.Numeric(25, 9)) # add by sophiarith on 2016-07-05
	OutstandingAmount 	=	db.Column(db.Numeric(25, 9))
	DisbursedStat 		=	db.Column(db.String(1))
	ValueDate 			= 	db.Column(db.String(20))
	Installment 		= 	db.Column(db.String(4))
	Term 				= 	db.Column(db.String(4))
	Cycle 				= 	db.Column(db.String(2))
	MaturityDate 		= 	db.Column(db.String(20))
	LoanProduct 		= 	db.Column(db.String(3))
	Category 			= 	db.Column(db.String(15))
	InterestRate 		= 	db.Column(db.String(11))
	IRR 				= 	db.Column(db.String(18))
	FreqType 			= 	db.Column(db.String(1))
	Frequency 			= 	db.Column(db.String(3))
	DeliqMode 			= 	db.Column(db.String(1))
	LoanPurpose 		= 	db.Column(db.String(3))
	ContractVB 			= 	db.Column(db.String(10)) #VB
	Group 				= 	db.Column(db.String(15))
	SourceOfFund 		= 	db.Column(db.String(4))
	TotalInterest 		= 	db.Column(db.Numeric(25, 9))
	AccrInterest 		= 	db.Column(db.Numeric(25, 9))
	AccrCurrentInt		=	db.Column(db.Numeric(25, 9))
	AccrIntCurrMonth 	=	db.Column(db.Numeric(25, 9))
	AccrIntPreMonth 	= 	db.Column(db.Numeric(25, 9))
	IntIncEarned		=	db.Column(db.Numeric(25, 9))
	AccrIntPerDay		=	db.Column(db.Numeric(25, 9))
	# ToDateTotalInt		=	db.Column(db.Numeric(25, 9))
	NextAccrDate 		= 	db.Column(db.String(20))
	Suspend 			=	db.Column(db.String(1))
	ContractOfficerID 	= 	db.Column(db.String(10))
	# Branch 				= 	db.Column(db.String(6))
	LoanType			= 	db.Column(db.String(1))
	AssetClass			= 	db.Column(db.String(2))
	MoreThanOneYear 	= 	db.Column(db.String(1))
	# Methodology 		=	db.Column(db.String(1))
	NextRunDate			=	db.Column(db.String(20))
	Penalty 			=	db.Column(db.String(1), default='Y')
	FirstCollectionDate =	db.Column(db.String(10))
	Classification 		=	db.Column(db.String(1), default='A')
	LoanStatus 			=	db.Column(db.String(3), default='SMT')
	Sector 				=	db.Column(db.String(3))
	Industry 			=	db.Column(db.String(3))
	Ownership 			=	db.Column(db.String(3))
	WatchDay 			= 	db.Column(db.String(5),default='0')
	FirstDueDate 		= 	db.Column(db.String(50))
	MinAssetClass 		=	db.Column(db.String(2),default='0')
	DoneWatchDay		=	db.Column(db.String(2),default='0')
	LegacyID 			=	db.Column(db.String(15))
	DisbursementDate    =   db.Column(db.String(10))
	LoanPurposeRemark   =   db.Column(db.Text)

	#For Leasing
	Vendor					= db.Column(db.String(13))
	AssetPrice				= db.Column(db.Numeric(25,9))
	DownPayment				= db.Column(db.Numeric(25,9))
	VATInput				= db.Column(db.Numeric(25,9))
	VATOutputOnLoan			= db.Column(db.Numeric(25,9))
	VATOutputOnDownPayment	= db.Column(db.Numeric(25,9))
	TaxExpense				= db.Column(db.Numeric(25,9))
	AssetDescription		= db.Column(db.Text)
	VATCollectionOption		= db.Column(db.String(1))

