from app.mktcore.mdlimports import *

class MKT_LOAN_PRODUCT(exmodel):
	ID 					= 	db.Column(db.String(3), primary_key=True, index=True)
	Description 		= 	db.Column(db.String(35))
	LocalDescription 	= 	db.Column(db.String(100))
	LNCategory 			= 	db.Column(db.String(15))
	InterestKey 		= 	db.Column(db.String(6))
	Rule 				= 	db.Column(db.String(3))
	Active 				= 	db.Column(db.String(1), default='Y')
	MinAge 				= 	db.Column(db.String(3))
	MaxAge 				= 	db.Column(db.String(3))
	IntDayBasis 		= 	db.Column(db.String(1))
	IntAccrBasis 		= 	db.Column(db.String(1))
	LoanPurpose 		= 	db.Column(db.String(3))
	Sector 				= 	db.Column(db.String(3))
	Industry 			= 	db.Column(db.String(3))
	DeliqMode 			= 	db.Column(db.String(1))
	RepMode 			= 	db.Column(db.String(1))
	BaseDateKey 		= 	db.Column(db.String(1))
	FWDBWDKey 			= 	db.Column(db.String(1))
	IntReceivableCate 	=	db.Column(db.String(15))
	IntIncomeCate 		=	db.Column(db.String(15))
	ReduceAmount 		=	db.Column(db.String(11))
	# For Myanmar
	TemplateBase 		= 	db.Column(db.String(12))
	# For Leasing
	Tax					=	db.Column(db.String(3))
	DownPaymentCategory	=	db.Column(db.String(15))

