from app.mktcore.mdlimports import *

class MKT_WRITE_OFF(exmodel):
	ID 					= 	db.Column(db.String(13), primary_key=True, index=True)
	CustomerID 			= 	db.Column(db.String(12))
	CustomerName 		= 	db.Column(db.String(70))
	Currency 			= 	db.Column(db.String(3))
	Disbursed 			= 	db.Column(db.Numeric(25, 9))
	Balance 			= 	db.Column(db.Numeric(25, 9))
	OverdueAmt 			= 	db.Column(db.Numeric(25, 9))
	WOFAmount 			= 	db.Column(db.Numeric(25, 9))
	Description 		=	db.Column(db.String(100))
	WOFDate 			= 	db.Column(db.String(10))
	WOFType 			=	db.Column(db.String(3), default='WOF')
	TotalRecoveryAmount =	db.Column(db.Numeric(25, 9))
	LastRecoveryDate 	= 	db.Column(db.String(10))

