from app.mktcore.mdlimports import *

class MKT_MANUAL_LOAN_COLLECTION(exmodel):
	ID 				=	db.Column(db.String(14), primary_key=True, index=True)
	LoanID			=	db.Column(db.String(14))
	CustomerID 		= 	db.Column(db.String(25))
	CustomerName 	= 	db.Column(db.String(35))
	LoanBalance 	= 	db.Column(db.Numeric(25,9))
	AccountID 		= 	db.Column(db.String(25))
	AccountBalance 	= 	db.Column(db.Numeric(25,9))
	Principal 		= 	db.Column(db.Numeric(25,9))
	PDPrincipal 	= 	db.Column(db.Numeric(25,9))
	Interest 		= 	db.Column(db.Numeric(25,9))
	PDInterest 		= 	db.Column(db.Numeric(25,9))
	Penalty 		= 	db.Column(db.Numeric(25,9))
	Charge 			= 	db.Column(db.Numeric(25,9))
	TranDate 		= 	db.Column(db.String(20))
	Reference 		= 	db.Column(db.String(35))
	Note 			= 	db.Column(db.String(100))
	NumInstallmentCollect 	= 	db.Column(db.String(3))

