from app.mktcore.mdlimports import *

class MKT_NBC_REPORT(exmodel):
	ID 				=	db.Column(db.String(12), primary_key=True, index=True)
	ReportTitle 	=	db.Column(db.String(100))
	ReportName 		=	db.Column(db.String(100))
	CompanyName 	=	db.Column(db.String(100))
	AsAt 			=	db.Column(db.String(20))
	Template 		=	db.Column(db.String(1))

