class MKT_NBC_REPORT_DE(exmodel):
	ID 				=	db.Column(db.String(12), primary_key=True, index=True)
	Report 			=	db.Column(db.String(12))
	LineNumber		=	db.Column(db.Integer, index=True)
	LineType 		=	db.Column(db.String(2))
	LineDescription = 	db.Column(db.String(100))
	LineValue 		=	db.Column(db.Text) # Base currency GL
	OtherLineValue 	=	db.Column(db.Text) # Other currency GL
	ProvLineValue 	=	db.Column(db.Text) # Provision in based currency GL
	Format 			=	db.Column(db.String(2))
	RowBGColor 		=	db.Column(db.String(15))
	Indent			=	db.Column(db.Integer)

