class MKT_NBC_INCOME_STATEMENT(exmodel):
	ID 						=	db.Column(db.Integer, primary_key=True, autoincrement=True)
	Year 					=	db.Column(db.String(4), primary_key=True, index=True)
	Month 					=	db.Column(db.String(6), primary_key=True, index=True)
	Branch 					=	db.Column(db.String(6), primary_key=True, index=True)
	Description 			=	db.Column(db.String(200))
	LineType 				=	db.Column(db.String(2))
	TotalInDollar 			=	db.Column(db.Numeric(25, 9))
	InRiel 					=	db.Column(db.Numeric(25, 9))
	OtherCurrencyInRiel 	=	db.Column(db.Numeric(25, 9))
	TotalInRiel 			=	db.Column(db.Numeric(25, 9))
	InThisYear 				=	db.Column(db.Numeric(25, 9))
	LineNumber 				=	db.Column(db.Integer)