from app.mktcore.mdlimports import *

class MKT_NOTIFICATION(exmodel):
	ID 			=	db.Column(db.Integer,primary_key=True, autoincrement=True)
	GlobalID 	=	db.Column(db.Text)
	AppName 	=	db.Column(db.String(50))
	Path 		=	db.Column(db.String(50))
	Title 		=	db.Column(db.String(50))
	Description =	db.Column(db.String(50))
	Read 		=	db.Column(db.Text)
	ReadDetail 	=	db.Column(db.Text)
	From 		=	db.Column(db.String(35))
	To 			=	db.Column(db.String(35))
	Message 	=   db.Column(db.Text)
	Clear 		=	db.Column(db.Text)
	RecordType  = 	db.Column(db.String(1), default='1')

class MKT_NOTIFICATION_READ(exmodel):
	ID 			=	db.Column(db.Integer,primary_key=True)
	Read 		=	db.Column(db.String(35),primary_key=True)

