#mony email and sms 2018-05-16
class MKT_NOTIFICATION_GATEWAY(exmodel):
	ID				=	db.Column(db.String(30), primary_key=True)
	Subject 		=	db.Column(db.Text)
	Message			=	db.Column(db.Text)
	To 				=	db.Column(db.String(50))
	NotifyStatus 	=	db.Column(db.String(1))# 1 success, 2 fail, 3 queue , 4 retry, 5: executing
	NotifyType  	=	db.Column(db.String(1))# 1 sms ,2 email
	Date			=	db.Column(db.String(10))
	Time			=	db.Column(db.String(8)) # Univeral Time
	Note 			=	db.Column(db.Text)
	Error 			= 	db.Column(db.Text)
	ExecuteID		= 	db.Column(db.String(30))
	Reference		=	db.Column(db.String(50)) 
	ResultDateTime	=	db.Column(db.String(20)) #store date/time when execute fail or success

