#MKT_OFFICER
class MKT_OFFICER(exmodel):
	ID 			=	db.Column(db.String(10), primary_key=True)
	FirstName 	=	db.Column(db.String(35))
	LastName 	=	db.Column(db.String(35))
	LocalFirstName = db.Column(db.String(35))
	LocalLastName  = db.Column(db.String(35))
	Phone 		=	db.Column(db.String(15))
	Phone2 		=	db.Column(db.String(15))
	Gender 		=	db.Column(db.String(6))
	StartDate 	=	db.Column(db.String(10))
	Position 	=	db.Column(db.String(3))
	ReportTo 	=	db.Column(db.String(10))
	Active 		=	db.Column(db.String(1))
	UserID 		=	db.Column(db.String(35))
