from app.mktcore.mdlimports import *

class MKT_OVERDRAFT_AMENDMENT(exmodel):
	ID 					= 	db.Column(db.String(14), primary_key=True, index=True)
	ODContractID		= 	db.Column(db.String(14))
	CustomerID			= 	db.Column(db.String(14))
	Operation 			=	db.Column(db.String(3))
	Currency 			=	db.Column(db.String(3))
	AIRAction 			=	db.Column(db.String(1))
	AIRAmount			=	db.Column(db.Numeric(25, 9))
	# PenaltyStatus		=	db.Column(db.String(1))
	# PenaltyAmount		=	db.Column(db.Numeric(25, 9))
	ValueDate 			=	db.Column(db.String(10))
	AmendDate 			=	db.Column(db.String(10))
	Term 				= 	db.Column(db.String(5))
	MaturityDate		=	db.Column(db.String(10))
	LimitedAmount 		=	db.Column(db.Numeric(25, 9))
	InterestRate 		=	db.Column(db.String(5))
	AccountType 		= 	db.Column(db.String(1))
	Note 				=	db.Column(db.String(50))

